mapper.select:
		select *
		from (select rownum rnum, TOTAL_ID, R_NUM, UUID, NICKNAME, R_CONTENTS, WRITE_DATE, UPDATE_DATE, 
            UPDATE_YN, DELETE_DATE, DELETE_YN
        from (select * from TB_REVIEW
        		where total_id = #{ totalId }
                order by write_date desc, R_NUM asc))
		where rnum between #{ startRow } and #{ endRow }
